    public void uploadbaidu(String ak, String sk) {
        new Thread() {
            @Override
            public void run() {
                // 获取token地址
                String authHost = "https://aip.baidubce.com/oauth/2.0/token?";
                String getAccessTokenUrl = authHost
                        // 1. grant_type为固定参数
                        + "grant_type=client_credentials"
                        // 2. 官网获取的 API Key
                        + "&client_id=" + ak
                        // 3. 官网获取的 Secret Key
                        + "&client_secret=" + sk;
                try {
                    URL realUrl = new URL(getAccessTokenUrl);
                    // 打开和URL之间的连接
                    HttpURLConnection connection = (HttpURLConnection) realUrl.openConnection();
                    connection.setRequestMethod("GET");
                    connection.connect();
                    // 获取所有响应头字段
                    Map<String, List<String>> map = connection.getHeaderFields();
                    // 遍历所有的响应头字段
                    for (String key : map.keySet()) {
                        System.err.println(key + "--->" + map.get(key));
                    }
                    // 定义 BufferedReader输入流来读取URL的响应
                    BufferedReader in = new BufferedReader(new InputStreamReader(connection.getInputStream()));
                    String urlresult = "";
                    String line;
                    while ((line = in.readLine()) != null) {
                        urlresult += line;
                    }
                   // System.err.println("result:" + result);
                    JSONObject jsonObject = new JSONObject(urlresult);
                    accesstoken = jsonObject.getString("access_token");

                    //上传对比
                    String url = "https://aip.baidubce.com/rest/2.0/face/v3/match?access_token=";
                    url += accesstoken;
                    Log.i("aaa", url);
//                    String face = EnrollActivity.Base64(facebyte);
//                    Log.i("aaa", Login_Activity.stu_ava);
//                    Log.i("aaa", face);
                    Login_Activity.stu_ava = Login_Activity.stu_ava.replaceAll("\n","");
                    face = face.replaceAll("\n","");

                    JSONObject js1 = new JSONObject();
                    JSONObject js2 = new JSONObject();
                    js1.put("image",Login_Activity.stu_ava);
                    js1.put("image_type","BASE64");
                    js1.put("face_type","LIVE");
                    js1.put("quality_control","LOW");
                    js1.put("liveness_control","LOW");
                    js2.put("image",face);
                    js2.put("image_type","BASE64");
                    js2.put("face_type","LIVE");
                    js2.put("quality_control","LOW");
                    js2.put("liveness_control","LOW");
                    JSONArray json = new JSONArray();
                    Log.i("aaa", js1.toString());
                    Log.i("aaa", js2.toString());
                    json.put(js1);
                    json.put(js2);
                    String baiduupload = json.toString().replaceAll("\\\\","");
                    OkHttpClient client = new OkHttpClient();

                    Log.i("aaa", baiduupload);
                    RequestBody requestBody = RequestBody.create(baiduupload,JSON);
                    Request request=new Request.Builder()
                            .url(url)
                            .header("Content-Type","application/json")
                            .post(requestBody)
                            .build();
                        //Log.i("aaa", response.body().string());
                    try {
                        Response response = client.newCall(request).execute();
                        String baiduresult = response.body().string();
                        Log.i("aaa", baiduresult);
                        JSONObject jsonresult = new JSONObject(baiduresult);
                        String result = jsonresult.getString("result");
                        Log.i("aaa", result);
                        JSONObject j = new JSONObject(result);
                        score = j.getDouble("score");
                        Log.i("aaa", score+"");

                        if (score >= 80)
                        {
                            issign = true;
                            uploadsigninfo();
                            Message msg = new Message();
                            msg.what = SIGNIN_SUCCESS;
                            handler.sendMessage(msg);

                        } else {
                            issign = false;
                            uploadsigninfo();
                            Message msg = new Message();
                            msg.what = SIGNIN_FAILED;
                            handler.sendMessage(msg);

                        }

                    } catch (IOException e) {
                        e.printStackTrace();
                    }


                } catch (Exception e) {
                    Log.i("aaa", "error! ");
                    Message msg = new Message();
                    msg.what = SIGNIN_ERROR;
                    handler.sendMessage(msg);
                    e.printStackTrace(System.err);
                }
            }
        }.start();

    }

